type MascotType = 1|2|3|4|5;
type Progress = {
  complete: number;
  total: number;
};

type props = {
  lessonId: string;
  mascotType: MascotType;
  progress?: Progress;
  subtitle?: string;
  title: string;
  description: string;
  buttonTextPrimary?: string;
  buttonTextSecondaryDownload?: string;
  buttonTextTertiary?: string;
};

export const LessonCard: FC<props> = ({
  lessonId,
  mascotType,
  progress,
  subtitle,
  title,
  description,
  buttonTextPrimary,
  buttonTextSecondaryDownload,
  buttonTextTertiary,
}) => {
  return (
    <Wrap>

      <BlockMascot>
        <DivMascot progress={progress}>
          {selectMascot(mascotType)}
        </DivMascot>
        {progress && (
          <DivProgress>
            <img src={urlGearSvg} />
            <span>{progress.completed}/{progress.total}</span>
          </DivProgress>
        )}
      </BlockMascot>

      <BlockLesson>
        <LessonDetails>

          <Subheading>{subtitle}</Subheading>
          <Heading>{title}</Heading>
          <Paragraph>{description}</Paragraph>

          {buttonTextPrimary && (
            <StyledButton type="primary" to={lessonId}>
              {buttonTextPrimary}
            </StyledButton>
          )}

          {buttonTextSecondaryDownload && (
            <StyledButton type="secondary-download" to={lessonId}>
              {buttonTextSecondaryDownload}
            </StyledButton>
          )}


          {buttonTextTertiary && (<>
            <Hr/>
            <StyledButton type="tertiary" to={lessonId}>
              {buttonTextTertiary}
            </StyledButton>
          </>)}

        </LessonDetails>
      </BlockLesson>

    </Wrap>
  );
};
